import uuid from 'uuid';
import {GET_ITEMS,ADD_ITEM,DELETE_ITEM} from '../actions/types';

const initialState={
    items:[
        { id:uuid(), name:'Eggs'},
        { id:uuid(), name:'Water'},
        { id:uuid(), name:'Steak'},
        { id:uuid(), name:'Milk'}
    ]
}

export default function(state=initialState,action){
    switch(action.type){
        case GET_ITEMS:
            return{
                ...state,

            };
        default:
            return state;
    }
}
